import CoinFlip from "./CoinFlip";
import { useLocation } from 'react-router-dom';
import { useState } from "react";

function Game() {
    const location = useLocation()
    const { name, bet, side } = location.state;
    const [flipState, startFlip] = useState('loading');

    let winningSide = Math.floor(Math.random() * 2) === 0 ? 'heads' : 'tails';

    return (
        <div>
            <CoinFlip animation={flipState} side={winningSide}/>
            <button onClick={() => {startFlip('ready')}} />
        </div>
    )
}

export default Game;
