import CoinFlip from "./CoinFlip";
import { useLocation } from 'react-router-dom';
import { useState } from "react";
import backReady from './coin-back-ready.svg';
import backLoading from './coin-back-loading.svg';
import Player from './Player';
import './Game.css';

function Game() {
    const location = useLocation()
    const { player1Name, player1Bet, player1Side } = location.state;
    const [player2Side, changePlayer2Side] = useState(null);
    const [flipState, setFlipState] = useState('loading');
    const [result, setResult] = useState('loading');

    let winningSide = Math.floor(Math.random() * 2) === 0 ? 'heads' : 'tails';

    return (
        <div className="Game">
            <Player name={player1Name} side={player1Side} result={player1Side === result ? '-winner' : ''}/>
            <CoinFlip animation={flipState} winningSide={winningSide} image={flipState === 'loading' ? backLoading : backReady}/>
            {player2Side === null ? <div style={{width: '300px', height: '150px'}}></div> : <Player name="me" side="heads" result={player2Side === result ? '-winner' : ''}/>}
            <button onClick={() => {setFlipState('ready'); setTimeout(() => {setResult(winningSide)}, 5000)}}>flip</button>
            <button onClick={() => {setFlipState('loading'); winningSide = Math.floor(Math.random() * 2) === 0 ? 'heads' : 'tails'}}>reset</button>
        </div>
    )
}

export default Game;
