import CoinFlip from "./CoinFlip";
import { useLocation } from 'react-router-dom';
import { useState } from "react";
import backReady from './coin-back-ready.svg';
import backLoading from './coin-back-loading.svg';
import Player from './Player';

function Game() {
    const location = useLocation()
    const { name, bet, side } = location.state;
    const [flipState, setFlipState] = useState('loading');

    let winningSide = Math.floor(Math.random() * 2) === 0 ? 'heads' : 'tails';

    return (
        <div className="Game">
            <Player name={name} side={side} />
            <CoinFlip animation={flipState} winningSide={winningSide} image={flipState === 'loading' ? backLoading : backReady}/>
            <Player name="me" side="heads" />
            <button onClick={() => {setFlipState('ready')}}>flip</button>
            <button onClick={() => {setFlipState('loading'); winningSide = Math.floor(Math.random() * 2) === 0 ? 'heads' : 'tails'}}>reset</button>
        </div>
    )
}

export default Game;
