import { useState } from 'react';
import './OverlayMenu.css';

function OverlayMenu(props) {
    const [active, changeActive] = useState(false);

    return (
        <div className={"side-menu" + (active ? ".active" : "")}>
            <div onClick={() => console.log("clicked overlay")} className={"side-menu__overlay" + (active ? ".active" : "")} />
            <div className={"side-menu__content" + (active ? ".active" : "")}>{props.content}</div>
        </div>
    );
}

export default OverlayMenu;
