import './OverlayMenu.css';

function OverlayMenu(props) {
    return (
        <div className={"side-menu" + (props.isMenuActive ? ".active" : "")}>
            <div onClick={props.closeMenu} className={"side-menu__overlay" + (props.isMenuActive ? ".active" : "")} />
            <div className={"side-menu__content" + (props.isMenuActive ? ".active" : "")}>{props.content}</div>
        </div>
    );
}

export default OverlayMenu;
